<template>
  <div id="side-handler" class="side-handler"></div>
</template>

<script lang="ts">
import { Component, Emit, Prop, Vue } from 'vue-property-decorator'

@Component
export default class SideHandler extends Vue {
  @Prop() private value!: number

  @Emit('resize')
  handleResize() {
    return this.deltaX
  }

  private deltaX = 0

  private endX = 0

  mounted() {
    let sideHandler = document.getElementById('side-handler') as HTMLElement
    if (sideHandler === null) return
    sideHandler.onmousedown = (e) => {
      let startX = e.clientX

      document.onmousemove = (e) => {
        let endX = e.clientX
        let deltaX = endX - startX
        startX = endX
        this.deltaX = deltaX
        this.endX = endX
        this.handleResize()
      }

      document.onmouseup = (e) => {
        document.onmousemove = null
        document.onmouseup = null
      }
    }
  }
}
</script>

<style lang="scss" scoped>
.side-handler {
  background: transparent
    url(data:image/png;base64,)
    50% no-repeat;
  cursor: col-resize;
  height: 100%;
  position: absolute;
  right: -10px;
  top: 0;
  width: 10px;
}
</style>
